#include <stdlib.h>

#include <stdio.h>
#include <string.h>
#include "get_next_line/get_next_line.h"

void print_issci(char *str)
{
    int L = 20;
    int i = 0;
    int k;
    int j = -1;
    int d = 0;
    int fd = open("ISSCI", O_RDONLY);
    char **tab = malloc(sizeof(char *) * (11 + 1));
    for (int i = 0; i < 11; i++)
    {

        tab[i] = malloc(sizeof(char) * 1025);
        tab[i] = get_next_line(fd);
    }
    tab[12] = 0;
    while (tab[++j])
    {
        i = -1;
        while (str[++i])
        {

            if (str[i] >= 'a' && str[i] <= 'z')
                k = (str[i] - 97) * L;
            else if (str[i] >= 'A' && str[i] <= 'Z')
                k = (str[i] - 65) * L;
            else
                k = strlen(tab[j]) - L;
            while (d++ < L)
                printf("%c", tab[j][k++]);
            d = 0;
        }
        printf("\n");
    }

}
int main()
{
    print_issci("ayix");
    return 0;
}

